// JavaScript Document
function winopen(win_id,win_title,win_url,win_width,win_height)
{
	var dg = new J.dialog({ 
	            id:win_id, 
	            title:win_title,
	            page:win_url,
	            width:win_width,
	            height:win_height,
	            bgcolor:"#000",
	            opacity:0.7,
	            cover:true,
	            autoSize:true,
				resize:true,
	            iconTitle:false,
				//titleBar:false,
				maxBtn:true,
				closeBtn:true,
				btnBar:true,
				lockScroll:false,
				xButton:true
	        }); 
        dg.ShowDialog();
}
